function pnpoly($pol, $x, $y) {
    $npol=count($pol);
    $xp=[];
    $yp=[];
    foreach ($pol as $item){
        $xp[]=$item['lat'];
        $yp[]=$item['lng'];
    };
    $c = false;
    for ($i = 0, $j = $npol - 1; $i < $npol; $j = $i++) {
        if (((($yp[$i] <= $y) && ($y < $yp[$j])) || (($yp[$j] <= $y) && ($y < $yp[$i]))) && ((($yp[$j] - $yp[$i]) != 0) && ($x > (($xp[$j] - $xp[$i]) * ($y - $yp[$i]) / ($yp[$j] - $yp[$i]) + $xp[$i])))){
            $c = !$c;
        }
    }
    return $c;
}
